mapper.select:

		SELECT
			material.material_info_id,
			material.material_order_code,
			material.material_order_code_desc,
			material.material_hnr_code,
			material.material_ckd_code,
			material.material_code,
			material.material_category,
			dictCate.`name` AS dictMaterialCategory,
			material.material_desc_cn,
			material.material_desc_en,
			material.material_desc_rn,
			material.hs_no,
			material.supplier_code,
			material.material_unit,
			dictUnit.`name` AS dictMaterialUnit,
			material.material_relation,
			material.material_relation_unit,
			dictRelationUnit.`name` AS dictMaterialRelationUnit,
			material.material_minpackage_amt,
			material.material_minpackage_type,
			dictMinpackageType.`name` AS dictMinpackageType,
			material.material_tax_price,
			material.material_vat_price,
			material.material_rate,
			material.material_currency,
			dictCurrency.`name` AS dictMaterialCurrency,
			material.material_price,
			material.level_bom_code,
			material.material_supply_mode,
			dictSupplyMode.`name` AS dictMaterialSupplyMode,
			material.factory_code,
			material.`length`,
			material.wide,
			material.height,
			material.data_role_at,
			material.user_defined1,
			material.user_defined2,
			material.user_defined3,
			material.user_defined4,
			material.user_defined5,
			material.user_defined6,
			material.user_defined7,
			material.user_defined8,
			material.user_defined9,
			material.user_defined10,
			material.is_locked,
			dictLockStatus.`name` AS dictLockStatus,
			material.create_user,
			material.create_time,
			material.update_user,
			material.update_time,
			material.is_delete,
			material.version
		FROM
			m_material_info material
		LEFT JOIN t_dict_data dictCate ON material.material_category = dictCate.`value`
		AND dictCate.dict_type_code = 'mattertype'
		AND dictCate.is_enable = 1
		AND dictCate.delete_flg = 0
		LEFT JOIN t_dict_data dictCurrency ON material.material_currency = dictCurrency.`value`
		AND dictCurrency.dict_type_code = 'currency'
		AND dictCurrency.is_enable = 1
		AND dictCurrency.delete_flg = 0
		LEFT JOIN t_dict_data dictUnit ON material.material_unit = dictUnit.`value`
		AND dictUnit.dict_type_code = 'unit'
		AND dictUnit.is_enable = 1
		AND dictUnit.delete_flg = 0
		LEFT JOIN t_dict_data dictRelationUnit ON material.material_relation_unit = dictRelationUnit.`value`
		AND dictRelationUnit.dict_type_code = 'unit'
		AND dictRelationUnit.is_enable = 1
		AND dictRelationUnit.delete_flg = 0
		LEFT JOIN t_dict_data dictSupplyMode ON material.material_supply_mode = dictSupplyMode.`value`
		AND dictSupplyMode.dict_type_code = 'supplyMode'
		AND dictSupplyMode.is_enable = 1
		AND dictSupplyMode.delete_flg = 0
		LEFT JOIN t_dict_data dictLockStatus ON material.is_locked = dictLockStatus.`value`
		AND dictLockStatus.dict_type_code = 'lockStatus'
		AND dictLockStatus.is_enable = 1
		AND dictLockStatus.delete_flg = 0
		LEFT JOIN t_dict_data dictMinpackageType ON material.material_minpackage_type = dictMinpackageType.`value`
		AND dictMinpackageType.dict_type_code = 'minpackageType'
		AND dictMinpackageType.is_enable = 1
		AND dictMinpackageType.delete_flg = 0
		WHERE
			material.is_delete = 0

    	AND material_info_id = #{materialInfoId,jdbcType=VARCHAR}


    	AND material_order_code = #{materialOrderCode,jdbcType=VARCHAR}


    	AND material_order_code_desc = #{materialOrderCodeDesc,jdbcType=VARCHAR}


    	AND material_ckd_code = #{materialCkdCode,jdbcType=VARCHAR}


    	AND material_code = #{materialCode,jdbcType=VARCHAR}


    	AND material_category = #{materialCategory,jdbcType=VARCHAR}



    	AND material_desc_cn LIKE #{materialDescCn_like,jdbcType=VARCHAR}



    	AND material_desc_en LIKE #{materialDescEn_like,jdbcType=VARCHAR}



    	AND material_desc_rn LIKE #{materialDescRn_like,jdbcType=VARCHAR}


    	AND hs_no = #{hsNo,jdbcType=VARCHAR}


    	AND supplier_code = #{supplierCode,jdbcType=VARCHAR}


    	AND material_unit = #{materialUnit,jdbcType=VARCHAR}


    	AND material_relation = #{materialRelation,jdbcType=VARCHAR}


    	AND material_relation_unit = #{materialRelationUnit,jdbcType=VARCHAR}


    	AND material_minpackage_amt = #{materialMinpackageAmt,jdbcType=DOUBLE}


    	AND material_minpackage_type = #{materialMinpackageType,jdbcType=VARCHAR}


    	AND material_tax_price = #{materialTaxPrice,jdbcType=DECIMAL}


    	AND material_vat_price = #{materialVatPrice,jdbcType=DECIMAL}


    	AND material_rate = #{materialRate,jdbcType=DECIMAL}


    	AND material_currency = #{materialCurrency,jdbcType=VARCHAR}


    	AND material_price = #{materialPrice,jdbcType=DECIMAL}


    	AND level_bom_code = #{levelBomCode,jdbcType=INTEGER}


    	AND material_supply_mode = #{materialSupplyMode,jdbcType=VARCHAR}


    	AND factory_code = #{factoryCode,jdbcType=VARCHAR}


        AND `length` = #{length,jdbcType=DECIMAL}


        AND wide = #{wide,jdbcType=DECIMAL}


        AND height = #{height,jdbcType=DECIMAL}


    	AND data_role_at = #{dataRoleAt,jdbcType=VARCHAR}


    	AND user_defined1 = #{userDefined1,jdbcType=VARCHAR}


    	AND user_defined2 = #{userDefined2,jdbcType=VARCHAR}


    	AND user_defined3 = #{userDefined3,jdbcType=VARCHAR}


    	AND user_defined4 = #{userDefined4,jdbcType=VARCHAR}


    	AND user_defined5 = #{userDefined5,jdbcType=VARCHAR}


    	AND user_defined6 = #{userDefined6,jdbcType=VARCHAR}


    	AND user_defined7 = #{userDefined7,jdbcType=VARCHAR}


    	AND user_defined8 = #{userDefined8,jdbcType=VARCHAR}


    	AND user_defined9 = #{userDefined9,jdbcType=VARCHAR}


    	AND user_defined10 = #{userDefined10,jdbcType=VARCHAR}


    	AND is_locked = #{isLocked,jdbcType=INTEGER}


    	AND material.create_user = #{createUser,jdbcType=VARCHAR}


    	AND material.create_time >= #{createTimeStart,jdbcType=VARCHAR}


        AND material.create_time <= #{createTimeEnd,jdbcType=VARCHAR}


    	AND material.update_user = #{updateUser,jdbcType=VARCHAR}


    	AND material.update_time >= #{updateTimeStart,jdbcType=VARCHAR}


        AND material.update_time <= #{updateTimeEnd,jdbcType=VARCHAR}

mapper.update:
	update m_material_info
	set update_user = #{updateUser,jdbcType=VARCHAR},update_time = DATE_FORMAT(now(),'%Y%m%d%H%m%S'),is_locked = #{isLocked,jdbcType=INTEGER} 
	where material_info_id IN

      #{item}

	update m_material_info
	set update_user = #{updateUser,jdbcType=VARCHAR},update_time = DATE_FORMAT(now(),'%Y%m%d%H%m%S'),is_delete = #{isDelete,jdbcType=INTEGER} 
	where material_info_id IN

      #{item}

mapper.select:
SELECT
FROM m_material_info WHERE is_delete = 0
AND version = #{version,jdbcType=INTEGER}
AND material_info_id = #{materialInfoId,jdbcType=VARCHAR}
