mapper.select:

		SELECT
			material.material_info_id,
			material.material_order_code,
			material.material_order_code_desc,
			material.material_hnr_code,
			material.material_ckd_code,
			material.material_code,
			material.material_category,
			dictCate.`name` AS dictMaterialCategory,
			material.material_desc_cn,
			material.material_desc_en,
			material.material_desc_rn,
			material.hs_no,
			material.supplier_code,
			t12.supplier_name_cn as supplierName,
			quota.material_quota as material_quota,
			material.material_unit,
			material.material_amount,
			dictUnit.`name` AS dictMaterialUnit,
			material.material_relation,
			dictConvertRelation.`name` AS dictConvertRelation,
			material.material_relation_unit,
			dictRelationUnit.`name` AS dictMaterialRelationUnit,
			material.material_minpackage_amt,
			material.material_minpackage_type,
			dictMinpackageType.`name` AS dictMinpackageType,
			material.material_tax_price,
			material.material_vat_price,
			material.tax,
			material.material_rate,
			material.material_currency,
			dictCurrency.`name` AS dictMaterialCurrency,
			material.material_price,
			material.material_loss_rate,
			material.level_bom_code,
			material.material_supply_mode,
			dictSupplyMode.`name` AS dictMaterialSupplyMode,
			material.factory_code,
			material.`length`,
			material.width,
			material.height,
			material.data_role_at,
			material.user_defined1,
			material.user_defined2,
			material.user_defined3,
			material.user_defined4,
			material.user_defined5,
			material.user_defined6,
			material.user_defined7,
			material.user_defined8,
			material.user_defined9,
			material.user_defined10,
			material.is_locked,
			dictLockStatus.`name` AS dictLockStatus,
			material.create_user,
			material.create_time,
			material.update_user,
			material.update_time,
			material.is_delete,
			material.version
		FROM
			m_material_info material
		LEFT JOIN t_material_quota quota ON material.material_code = quota.material_code 
		AND material.supplier_code = quota.supplier_code
		AND material.material_code = quota.material_code
		LEFT JOIN t_dict_data_${languageFlag} dictCate ON material.material_category = dictCate.`value`
		AND dictCate.dict_type_code = 'mattertype'
		AND dictCate.is_enable = 1
		AND dictCate.delete_flg = 0
		LEFT JOIN t_dict_data_${languageFlag} dictCurrency ON material.material_currency = dictCurrency.`value`
		AND dictCurrency.dict_type_code = 'currency'
		AND dictCurrency.is_enable = 1
		AND dictCurrency.delete_flg = 0
		LEFT JOIN t_dict_data_${languageFlag} dictUnit ON material.material_unit = dictUnit.`value`
		AND dictUnit.dict_type_code = 'unit'
		AND dictUnit.is_enable = 1
		AND dictUnit.delete_flg = 0
		LEFT JOIN t_dict_data_${languageFlag} dictRelationUnit ON material.material_relation_unit = dictRelationUnit.`value`
		AND dictRelationUnit.dict_type_code = 'unit'
		AND dictRelationUnit.is_enable = 1
		AND dictRelationUnit.delete_flg = 0
		LEFT JOIN t_dict_data_${languageFlag} dictSupplyMode ON material.material_supply_mode = dictSupplyMode.`value`
		AND dictSupplyMode.dict_type_code = 'supplyMode'
		AND dictSupplyMode.is_enable = 1
		AND dictSupplyMode.delete_flg = 0
		LEFT JOIN t_dict_data_${languageFlag} dictLockStatus ON material.is_locked = dictLockStatus.`value`
		AND dictLockStatus.dict_type_code = 'lockStatus'
		AND dictLockStatus.is_enable = 1
		AND dictLockStatus.delete_flg = 0
		LEFT JOIN t_dict_data_${languageFlag} dictMinpackageType ON material.material_minpackage_type = dictMinpackageType.`value`
		AND dictMinpackageType.dict_type_code = 'minpackageType'
		AND dictMinpackageType.is_enable = 1
		AND dictMinpackageType.delete_flg = 0
		LEFT JOIN t_dict_data_${languageFlag} dictConvertRelation ON material.material_relation = dictConvertRelation.`value`
		AND dictConvertRelation.dict_type_code = 'converRelation'
		AND dictConvertRelation.is_enable = 1
		AND dictConvertRelation.delete_flg = 0
	    LEFT JOIN m_supplier_info AS t12 ON material.supplier_code = t12.supplier_code
	    AND t12.is_delete = 0

		WHERE
			material.is_delete = 0


    	AND material.material_info_id = #{materialInfoId,jdbcType=VARCHAR}



    	AND material.material_order_code LIKE #{materialOrderCode_like,jdbcType=VARCHAR}



    	AND material.material_order_code_desc LIKE #{materialOrderCodeDesc_like,jdbcType=VARCHAR}



    	AND material.material_ckd_code LIKE #{materialCkdCode_like,jdbcType=VARCHAR}



    	AND material.material_code LIKE #{materialCode_like,jdbcType=VARCHAR}


    	AND material.material_category = #{materialCategory,jdbcType=VARCHAR}



    	AND material.material_desc_cn LIKE #{materialDescCn_like,jdbcType=VARCHAR}



    	AND material.material_desc_en LIKE #{materialDescEn_like,jdbcType=VARCHAR}



    	AND material.material_desc_rn LIKE #{materialDescRn_like,jdbcType=VARCHAR}



    	AND material.hs_no LIKE #{hsNo_like,jdbcType=VARCHAR}



    	AND material.supplier_code LIKE #{supplierCode_like,jdbcType=VARCHAR}


    	AND material.material_amount = #{materialAmount,jdbcType=DECIMAL}


    	AND material.material_unit = #{materialUnit,jdbcType=VARCHAR}


    	AND material.material_relation = #{materialRelation,jdbcType=VARCHAR}


    	AND material.material_relation_unit = #{materialRelationUnit,jdbcType=VARCHAR}


    	AND material.material_minpackage_amt = #{materialMinpackageAmt,jdbcType=DECIMAL}


    	AND material.material_minpackage_type = #{materialMinpackageType,jdbcType=VARCHAR}


    	AND material.material_tax_price = #{materialTaxPrice,jdbcType=DECIMAL}


    	AND material.material_vat_price = #{materialVatPrice,jdbcType=DECIMAL}


    	AND material.tax = #{tax,jdbcType=DECIMAL}


    	AND material.material_rate = #{materialRate,jdbcType=DECIMAL}


    	AND material.material_currency = #{materialCurrency,jdbcType=VARCHAR}


    	AND material.material_price = #{materialPrice,jdbcType=DECIMAL}


    	AND material.material_loss_rate = #{materialLossRate,jdbcType=DECIMAL}


    	AND material.level_bom_code = #{levelBomCode,jdbcType=INTEGER}


    	AND material.material_supply_mode = #{materialSupplyMode,jdbcType=VARCHAR}


    	AND material.factory_code = #{factoryCode,jdbcType=VARCHAR}


        AND `length` = #{length,jdbcType=DECIMAL}


        AND width = #{width,jdbcType=DECIMAL}


        AND height = #{height,jdbcType=DECIMAL}


    	AND material.data_role_at = #{dataRoleAt,jdbcType=VARCHAR}


    	AND material.user_defined1 = #{userDefined1,jdbcType=VARCHAR}


    	AND material.user_defined2 = #{userDefined2,jdbcType=VARCHAR}


    	AND material.user_defined3 = #{userDefined3,jdbcType=VARCHAR}


    	AND material.user_defined4 = #{userDefined4,jdbcType=VARCHAR}


    	AND material.user_defined5 = #{userDefined5,jdbcType=VARCHAR}


    	AND material.user_defined6 = #{userDefined6,jdbcType=VARCHAR}


    	AND material.user_defined7 = #{userDefined7,jdbcType=VARCHAR}


    	AND material.user_defined8 = #{userDefined8,jdbcType=VARCHAR}


    	AND material.user_defined9 = #{userDefined9,jdbcType=VARCHAR}


    	AND material.user_defined10 = #{userDefined10,jdbcType=VARCHAR}


    	AND material.is_locked = #{isLocked,jdbcType=INTEGER}


    	AND material.version = #{version,jdbcType=INTEGER}



    	AND material.create_user LIKE #{createUser_like,jdbcType=VARCHAR}


    	AND material.create_time >= #{createTimeStart,jdbcType=VARCHAR}


        AND material.create_time <= #{createTimeEnd,jdbcType=VARCHAR}



    	AND material.update_user LIKE #{updateUser_like,jdbcType=VARCHAR}


    	AND material.update_time >= #{updateTimeStart,jdbcType=VARCHAR}


        AND material.update_time <= #{updateTimeEnd,jdbcType=VARCHAR}

