	where material_info_id IN

      #{item}

	update m_material_info
	set update_user = #{updateUser,jdbcType=VARCHAR},update_time = DATE_FORMAT(now(),'%Y%m%d%H%m%S'),is_delete = #{isDelete,jdbcType=INTEGER} 
	where material_info_id IN

      #{item}

mapper.select:
SELECT
FROM m_material_info WHERE is_delete = 0
AND version = #{version,jdbcType=INTEGER}
AND material_info_id = #{materialInfoId,jdbcType=VARCHAR}

select t1.* from  m_material_info AS t1 where t1.is_delete = 0
AND t1.material_code = #{materialCode}
and t1.data_role_at =#{dataRoleAt}

SELECT

FROM
	m_material_info materialInfo
WHERE
	 materialInfo.data_role_at = #{dataRoleAt}
AND materialInfo.material_info_id = #{id,jdbcType=VARCHAR}
