mapper.select:
SELECT t1.*, t2. NAME as dictUserType, t3. NAME as dictUserStatus FROM m_user_info AS t1 LEFT JOIN t_dict_data AS t2 ON t1.user_type = t2.`value` AND t2.dict_type_code = 'usertype' AND t2.delete_flg = 0 LEFT JOIN t_dict_data AS t3 ON t1.user_status = t3.`value` AND t3.dict_type_code = 'userstatus' AND t3.delete_flg = 0 WHERE t1.is_delete = 0
AND t1.user_name LIKE #{userName_like}
AND t1.user_real_name LIKE #{userRealName_like}

SELECT

FROM
m_user_info
WHERE
is_delete = 0

    AND user_name  = #{userName,jdbcType=VARCHAR}


    AND version  = #{version,jdbcType=INTEGER}


    AND user_id  = #{userId,jdbcType=VARCHAR}
