mapper.select:

select 

from t_dict_data
where delete_flg = 0

    AND dict_type_code = #{dictTypeCode,jdbcType=VARCHAR}


    AND value = #{value,jdbcType=VARCHAR}


    AND name = #{name,jdbcType=VARCHAR}


    AND desc = #{desc,jdbcType=VARCHAR}


    AND sort_code = #{sortCode,jdbcType=INTEGER}


    AND is_enable = #{isEnable,jdbcType=INTEGER}



    AND remark LIKE #{remark_like}



    AND create_user LIKE #{createUser_like}


    AND create_time = #{createTime,jdbcType=VARCHAR}



    AND update_user LIKE #{updateUser_like}


    AND update_time = #{updateTime,jdbcType=VARCHAR}


    AND version = #{version,jdbcType=INTEGER}

mapper.update:

update t_dict_data
set 
  update_user = #{updateUser,jdbcType=VARCHAR},
  update_time = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s'),
  delete_flg = #{deleteFlg,jdbcType=INTEGER}
where dict_type_code = #{dictTypeCode,jdbcType=VARCHAR}
