mapper.select:

select 
t1.*,t2.name as typeName
from t_dict_data_${languageFlag} as t1
left join t_dict_type_${languageFlag} as t2 on t1.dict_type_code=t2.code and t2.is_delete=0
where delete_flg = 0

  AND t1.id = #{id,jdbcType=VARCHAR}


    AND t1.dict_type_code = #{dictTypeCode,jdbcType=VARCHAR}


    AND t1.value = #{value,jdbcType=VARCHAR}


    AND t1.name = #{name,jdbcType=VARCHAR}


    AND t1.desc = #{desc,jdbcType=VARCHAR}


    AND t1.sort_code = #{sortCode,jdbcType=INTEGER}


    AND t1.is_enable = #{isEnable,jdbcType=INTEGER}



    AND t1.remark LIKE #{remark_like}



    AND t1.create_user LIKE #{createUser_like}


    AND t1.create_time = #{createTime,jdbcType=VARCHAR}



    AND t1.update_user LIKE #{updateUser_like}


    AND t1.update_time = #{updateTime,jdbcType=VARCHAR}


    AND t1.version = #{version,jdbcType=INTEGER}

mapper.update:

update t_dict_data_${languageFlag}
set 
  update_user = #{updateUser,jdbcType=VARCHAR},
  update_time = DATE_FORMAT(NOW(),'%Y%m%d%H%i%s'),
  delete_flg = #{deleteFlg,jdbcType=INTEGER}
where dict_type_code = #{dictTypeCode,jdbcType=VARCHAR}
