mapper.select:
SELECT quota.material_code, quota.supplier_code, quota.supplier_name, quota.material_quota, quota.data_role_at, quota.create_user, quota.create_time, quota.update_user, quota.update_time, quota.is_delete, quota.version FROM t_material_quota quota WHERE quota.is_delete = 0
AND quota.material_code LIKE #{materialCode_like,jdbcType=VARCHAR}
AND quota.supplier_code LIKE #{supplierCode_like,jdbcType=VARCHAR}
AND quota.supplier_name LIKE #{supplierName_like,jdbcType=VARCHAR}
AND quota.material_quota = #{materialQuota,jdbcType=DECIMAL}
AND quota.data_role_at = #{dataRoleAt,jdbcType=VARCHAR}
