mapper.select:
select
from t_order_info where id IN
#{item}

	SELECT
		orderInfo.id,
		orderInfo.order_code,
		orderInfo.order_code_desc,
		orderInfo.order_amount,
		orderInfo.order_date,
		orderInfo.order_unit,
	    dictUnit.`name` AS dictOrderUnit,
		orderInfo.order_status,
        orderInfo.order_type,
        dictOrderType.`name` as dictOrderType,
	    dictPlanStatus.`name` AS dictOrderStatus,
	    orderInfo.data_role_at,
	    orderInfo.is_balance,
	    orderInfo.date_flag,
		orderInfo.create_user,
		orderInfo.create_time,
		orderInfo.update_user,
		orderInfo.update_time,
		orderInfo.is_delete,
		orderInfo.version
	FROM
		t_order_info orderInfo
	LEFT JOIN t_dict_data dictUnit ON orderInfo.order_unit = dictUnit.`value`
	AND dictUnit.dict_type_code = 'unit'
	AND dictUnit.is_enable = 1
	AND dictUnit.delete_flg = 0
	LEFT JOIN t_dict_data dictPlanStatus ON orderInfo.order_status = dictPlanStatus.`value`
	AND dictPlanStatus.dict_type_code = 'planStatus'
	AND dictPlanStatus.is_enable = 1
	AND dictPlanStatus.delete_flg = 0
    LEFT JOIN t_dict_data dictOrderType ON orderInfo.order_type = dictOrderType.`value`
    AND dictOrderType.dict_type_code = 'orderType'
    AND dictOrderType.is_enable = 1
    AND dictOrderType.delete_flg = 0
	WHERE
		orderInfo.is_delete = 0

		AND data_role_at = #{dataRoleAt,jdbcType=VARCHAR}


		AND orderInfo.id = #{id,jdbcType=VARCHAR}


		AND orderInfo.is_balance = #{isBalance,jdbcType=INTEGER}



    	AND orderInfo.order_code LIKE #{orderCode_like,jdbcType=VARCHAR}



    	AND orderInfo.order_code_desc LIKE #{orderCodeDesc_like,jdbcType=VARCHAR}


    	AND orderInfo.order_unit = #{orderUnit,jdbcType=VARCHAR}


    	AND orderInfo.order_status = #{orderStatus,jdbcType=VARCHAR}


      AND orderInfo.order_type = #{orderType,jdbcType=VARCHAR}


    	AND orderInfo.date_flag = #{dateFlag,jdbcType=VARCHAR}


    	AND orderInfo.order_amount = #{orderAmount,jdbcType=DECIMAL}


    	AND orderInfo.order_date >= #{orderDateStart,jdbcType=VARCHAR}


        AND orderInfo.order_date <= #{orderDateEnd,jdbcType=VARCHAR}



    	AND orderInfo.create_user LIKE #{createUser_like,jdbcType=VARCHAR}


    	AND orderInfo.create_time >= #{createTimeStart,jdbcType=VARCHAR}


        AND orderInfo.create_time <= #{createTimeEnd,jdbcType=VARCHAR}



    	AND orderInfo.update_user LIKE #{updateUser_like,jdbcType=VARCHAR}


    	AND orderInfo.update_time >= #{updateTimeStart,jdbcType=VARCHAR}


        AND orderInfo.update_time <= #{updateTimeEnd,jdbcType=VARCHAR}

    order by orderInfo.order_date desc,orderInfo.order_code

mapper.update:
	update t_order_info
	set update_user = #{updateUser,jdbcType=VARCHAR},update_time = DATE_FORMAT(now(),'%Y%m%d%H%m%S'),is_delete = #{isDelete,jdbcType=INTEGER} 
	where id IN

      #{item}

mapper.select:
select * from t_order_info as t1 where t1.is_delete=0
and t1.data_role_at=#{dataRoleAt}
and t1.is_balance = #{isBalance,jdbcType=INTEGER}

select t1.* from  t_order_info AS t1 where t1.is_delete = 0
AND t1.order_code = #{orderCode}
and t1.data_role_at =#{dataRoleAt}
and t1.is_balance = #{isBalance,jdbcType=INTEGER}
